package qualityCBhomeWork;

public class evenArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int eveoddA[] = { 2, 5, 7, 4, 20, 190, 24, 9, 1 };

		for (int i = 0; i <= eveoddA.length; i++) {

			if (eveoddA[i] % 2 == 0) {
				System.out.println(eveoddA[i] + " is Even #");
			}

		}

	}

}
